package week1.day1;

public class LearnForIFElse {
	// print all the even numbers between 1 and 50
	// find even number - if
	// find all the even numbers - for
	// combine both if and for to achieve this

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 50; i++) {
			if (i % 2 == 0) {
				System.out.println(i);
			}
		}
	}

}
